definition.script:
#!groovy
import groovy.json.*
def static_result = 'success'
def filenum = 0
def snippetnum = 0
def totals = 0

pipeline {
	agent {
		node { 
			label "vm-slave-scan" 
		}
	}
	parameters {
		string(name: 'merge_dir', defaultValue: 'CODE')
		string(name: 'PULL_NUMBER', defaultValue: '')
		string(name: 'PULL_BASE_REF', defaultValue: '')
		string(name: 'giteeTargetRepoHttpUrl', defaultValue: 'https://gitee.com')
		string(name: 'REPO_OWNER', defaultValue: '')
		string(name: 'REPO_NAME', defaultValue: '')		
   }
	stages {
		stage ('SCANOSS')  {
		    agent {
				node { 
					label "vm-slave-scan" 
				}
			}
			steps ('sonar-steps') {
				script {
					try {
						cleanWs()
						sh "ls -al ${WORKSPACE}"
						echo "-------------------git pull merge-------------------"
						checkout(
							[$class: 'GitSCM',
							 branches: [[name: 'pull/${PULL_NUMBER}/MERGE']],
							 doGenerateSubmoduleConfigurations: false,
							 extensions: [[$class: 'RelativeTargetDirectory',
							 relativeTargetDir: '${merge_dir}'],
							 [$class: 'CleanBeforeCheckout']],
							 submoduleCfg: [],
							 userRemoteConfigs: [[credentialsId: 'i-am-a-robot',
							 name: 'origin',
							 refspec: '+refs/heads/*:refs/remotes/origin/* +refs/pull/*/MERGE:refs/pull/*/MERGE',
							 url: '${giteeTargetRepoHttpUrl}/${REPO_OWNER}/${REPO_NAME}.git']]])
						echo "-------------------deal modify code-------------------"
						sh '''
							rm -rf ${WORKSPACE}/${merge_dir}/modify_dir
							mkdir -p ${WORKSPACE}/${merge_dir}/modify_dir
							cd ${WORKSPACE}/${merge_dir}
							git diff-tree -r --name-only --no-commit-id origin/${PULL_BASE_REF} HEAD > modify_list.txt
							cat modify_list.txt
							while read -r line
							do
								dir_name=${line%/*}
								file_name=${line##*/}
								if [ $(echo $line |grep '/') ];then
									mkdir -p ${WORKSPACE}/${merge_dir}/modify_dir/${dir_name}
									if [ -f "${WORKSPACE}/${merge_dir}/${line}" ]; then
										cp -rf ${WORKSPACE}/${merge_dir}/${line} ${WORKSPACE}/${merge_dir}/modify_dir/${dir_name}
									fi
								else
									mkdir -p ${WORKSPACE}/${merge_dir}/modify_dir
									if [ -f "${WORKSPACE}/${merge_dir}/${line}" ]; then
										cp -rf ${WORKSPACE}/${merge_dir}/${line} ${WORKSPACE}/${merge_dir}/modify_dir
									fi
								fi
							done < ${WORKSPACE}/${merge_dir}/modify_list.txt
							#find ${WORKSPACE}/${merge_dir}/modify_dir -type f
						'''
						sh '''
							/usr/local/python3/bin/scanoss-py scan --ignore /opt/openlookeng/SBOM.json --key cd849a65500b424fc82c6236626ddb1b --apiurl https://shenzhen.scanoss.com/api/scan/direct ${WORKSPACE}/${merge_dir}/modify_dir  > /var/www/html/${JOB_NAME}--${BUILD_NUMBER}.json
						'''
							def inputFile = readFile("/var/www/html/${JOB_NAME}--${BUILD_NUMBER}.json")

							def InputJSON = new JsonSlurper().parseText(inputFile)

										def info="<a href='http://119.13.87.120/${JOB_NAME}--${BUILD_NUMBER}.json' download='result.json'>Download Full json result</a>"
							            def dat="<table border='1' cellspacing='0' cellpadding='5' style='margin-top:20px'><tr><td style='1px solid black'>filename</td><td>id</td><td>lines</td><td>oss_lines</td><td>matched</td><td>vendor</td><td>component</td><td>version</td><td>url</td><td>file</td><td>file_id</td><td>licenses</td><td>vulnerabilities</td></tr>"

								InputJSON.each { def k ,def value ->
											value.each{def v ->

													if (v.id == "snippet") { 
														snippetnum++ 

													}
													if (v.id == "file") {
														filenum++

													}
													if (v.id != "none") {
														def result="<tr><td>${k}</td><td>${v.id}</td><td>${v.lines}</td><td>${v.oss_lines}</td><td>${v.matched}</td><td>${v.vendor}</td><td>${v.component}</td><td>${v.version}</td><td>${v.url}</td><td>${v.file}</td><td>${v.file_id}</td><td>${v.licenses}</td><td nowrap = 'nowrap' title = '${v.vulnerabilities}'>${v.vulnerabilities}</td></tr>"
														dat=dat + result
														}
													} 
												}
							totals = snippetnum + filenum				
							def sums = "<table border='1' cellspacing='0' cellpadding='5' style='margin-top:20px'><tr><td style='1px solid black'>snippet</td><td>file</td><td>total</td></tr><tr><td>${snippetnum}</td><td>${filenum}</td><td>${totals}</td></tr></table>"					
							dat=info + sums + dat + "</table>"


					    echo "#################${dat}"
					    // sh	'''
				        // 	echo ${dat} > /var/www/html/${JOB_NAME}--${BUILD_NUMBER}.html
					    // '''
					    writeFile(file: "/var/www/html/${JOB_NAME}--${BUILD_NUMBER}.html", text: "${dat}")

					}
					catch(all) {
						static_result=sh(returnStdout: true, script: 'echo ${all}')

					}
				}
			}
		}
	}

	post {
		success {
			script {
				retry(3) {
				    echo "-------------------post success-------------------"

					def con = "snippet num: ${snippetnum}  file num: ${filenum}"
					// addGiteeMRComment comment:  "|  SCANOSS  | :white_check_mark: **SUCCESS**  |  [#${BUILD_ID}](http://119.13.87.120/${JOB_NAME}--${BUILD_NUMBER}.html) | ${con} |\n|  -------------  |  -------------  |  -------------  |  -------------  |\n"
					echo "http://119.13.87.120/${JOB_NAME}--${BUILD_NUMBER}.html"

				}
			}
		}
		unsuccessful {
			script {
				retry(3) {
				    echo "-------------------post failed-------------------"
					// addGiteeMRComment comment: "|  SCANOSS  | :x: **FAILURE**   |  [#${BUILD_ID}](http://119.13.87.120/${JOB_NAME}--${BUILD_NUMBER}.html)   |\n|  -------------  |  -------------  |  -------------   |\n"

				    echo "http://119.13.87.120/${JOB_NAME}--${BUILD_NUMBER}.html"
				}
			}
		}
	}
} // pipeline end
