hudson.plugins.postbuildtask.TaskPropert.script:
function delete_labels() { echo "Delete labels" curl -X DELETE "https://gitee.com/api/v5/repos/opengauss/openGauss-server/pulls/$1/labels/$3" -H "Authorization: Bearer $2" -H 'Content-Type: application/json;charset=UTF-8' > /dev/null }
delete_labels ${giteePullRequestIid} ${token} ci-pipeline-running
curl -X POST "https://gitee.com/api/v5/repos/opengauss/openGauss-server/pulls/${giteePullRequestIid}/labels" \ -H "Authorization: Bearer ${token}" \ -H "Content-Type: application/json;charset=UTF-8" \ -d '["ci-pipeline-failed"]' > /dev/null
