hudson.tasks.Shell.command:
#!/bin/bash -l
#to recover data from obs with specify date,the last two parameters is vhostname and es's indexname,if not assignment,it will use the resource/conf.properties parameters in the jar
month=$(date -d "-1 day" "+%m")
day=$(date -d "-1 day" "+%d")
pw=${ES_PASS}
NODE_TLS_REJECT_UNAUTHORIZED=0 /usr/local/bin/elasticdump  --input=https://admin:$pw@119.8.111.61:9200 --input-index=logstash-2022.$month.$day --output=/mnt/mount_path/openlookeng-download-2022.$month.$day-hk-app.json  --searchBody '{"query":{"terms":{"vhost": ["download.openlookeng.io"]}}}' --type=data --maxSockets=4 --timeout=60000 --retryAttempts=10 --retryDelay=30000 --limit=1000
