hudson.tasks.Shell.command:
#!/bin/bash set -e IMAGE_VERSOPM="v0.0.1" GIT_EMAIL="tommylike@gmail.com" MOUDLE_NAME=${PROJECT_NAME##*/} echo "$PROJECT_NAME" if hash git 2>/dev/null; then IMAGE_VERSION=$(git rev-parse HEAD) fi
if hash docker 2>/dev/null; then docker login -u ap-southeast-1@$DOCKER_USER -p $DOCKER_PASS swr.ap-southeast-1.myhuaweicloud.com else echo "docker not installed" fi cd $WORKSPACE docker build -f $PROJECT_NAME/Dockerfile -t swr.ap-southeast-1.myhuaweicloud.com/opensourceway/prow/$MOUDLE_NAME:$IMAGE_VERSION . docker push swr.ap-southeast-1.myhuaweicloud.com/opensourceway/prow/$MOUDLE_NAME:$IMAGE_VERSION docker rmi swr.ap-southeast-1.myhuaweicloud.com/opensourceway/prow/$MOUDLE_NAME:$IMAGE_VERSION if hash git 2>/dev/null; then git config --global user.email $GIT_EMAIL git config --global user.name $GIT_USER git clone https://$GIT_USER:$GIT_PASS@github.com/opensourceways/infra-common cd infra-common/common-applications/control/prow else echo "git is not exiting" fi if hash kustomize 2>/dev/null; then kustomize edit set image swr.ap-southeast-1.myhuaweicloud.com/opensourceway/prow/$MOUDLE_NAME:$IMAGE_VERSION else echo "failed replace ${MOUDLE_NAME} image" fi
if hash git 2>/dev/null; then git add . git commit -m "update ${MOUDLE_NAME} image to ${IMAGE_VERSION}" git push origin --all else echo "git is not installed" fi
