hudson.tasks.Shell.command:
#!/bin/bash shell_path=/home/jenkins/ci_check . $shell_path/src/lib/lib.sh
# 从文件服务器拷贝文件 function scp_comment_file() { log_info "***** Start to scp comment file *****" fileserver_tmpfile_path="/repo/soe${repo_server_test_tail}/check_item" scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null root@${repo_server}:$fileserver_tmpfile_path/${check_item_comment_aarch64} . || log_info "file ${check_item_comment_aarch64} not exist" scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null root@${repo_server}:$fileserver_tmpfile_path/${check_item_comment_x86} . || log_info "file ${check_item_comment_x86} not exist" #ls $WORKSPACE/${comment} scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null root@${repo_server}:$fileserver_tmpfile_path/${compare_package_result_aarch64} . || log_info "file ${compare_package_result_aarch64} not exist" scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null root@${repo_server}:$fileserver_tmpfile_path/${compare_package_result_x86} . || log_info "file ${compare_package_result_x86} not exist" ls $WORKSPACE/${compare_result} scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null root@${repo_server}:$fileserver_tmpfile_path/${build_num_file} . || log_info "file ${build_num_file} not exist" log_info "***** End to scp comment file *****" }
# 执行入口 function main() { log_info "save build num file" if [[ -e $build_num_file ]]; then scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR ${build_num_file} root@${repo_server}:$fileserver_tmpfile_path/${build_num_file} fi } #. ${shell_path}/src/lib/comment.sh "$repo" "$prid" "$committer" "$giteetoken" "$jenkins_user" "$jenkins_api_token" "$WORKSPACE" "$SaveBuildRPM2Repo" "$repo_server" "$compare_result" "$commentid" "https://jenkins.openeuler.org/"
set +x set -e main set -x set +e
