hudson.tasks.Shell.command:
#!/bin/bash
set +x
function config_dockerfile() { cat > Dockerfile << "FEOF" #FROM swr.cn-north-4.myhuaweicloud.com/openeuler/openjdk/11-jdk-stretch:20.03-lts-sp3 FROM swr.cn-north-4.myhuaweicloud.com/openeuler/openjdk/11-jdk-stretch:22.03-lts
RUN set -eux; \ yum install -y curl sudo expect openssh shadow git python3 osc python openssh-clients tar wget bc
ARG VERSION=4.3 ARG user=jenkins ARG group=jenkins ARG uid=1000 ARG gid=1000 ARG AGENT_WORKDIR=/home/${user}/agent
RUN curl --create-dirs -fsSLo /usr/share/jenkins/agent.jar https://repo.jenkins-ci.org/public/org/jenkins-ci/main/remoting/${VERSION}/remoting-${VERSION}.jar \ && chmod 755 /usr/share/jenkins \ && chmod 644 /usr/share/jenkins/agent.jar \ && ln -sf /usr/share/jenkins/agent.jar /usr/share/jenkins/slave.jar
RUN curl --create-dirs -fsSLo /usr/local/bin/jenkins-agent http://121.36.53.23/AdoptOpenJDK/jenkins-agent #COPY jenkins-agent /usr/local/bin/jenkins-agent
RUN chmod a+rx /usr/local/openjdk-11 \ && chmod a+rx /usr/local/bin/jenkins-agent \ && ln -s /usr/local/bin/jenkins-agent /usr/local/bin/jenkins-slave
RUN groupadd -g ${gid} ${group} RUN useradd -c "Jenkins user" -d /home/${user} -u ${uid} -g ${gid} -m ${user} RUN echo "${user} ALL=(ALL) NOPASSWD:ALL" >> /etc/sudoers
USER ${user} ENV AGENT_WORKDIR=${AGENT_WORKDIR} RUN mkdir /home/${user}/.jenkins && mkdir -p ${AGENT_WORKDIR}
VOLUME /home/${user}/.jenkins VOLUME ${AGENT_WORKDIR} WORKDIR ${AGENT_WORKDIR}
ENTRYPOINT ["jenkins-agent"] FEOF }
config_obs_dockerfile() { cat > Dockerfile << "FEOF" #FROM swr.cn-north-4.myhuaweicloud.com/openeuler/openjdk/11-jdk-stretch:20.03-lts-sp3 FROM swr.cn-north-4.myhuaweicloud.com/openeuler/openjdk/11-jdk-stretch:22.03-lts
RUN cat /etc/yum.repos.d/openEuler.repo RUN sed -i "s/repo.huaweicloud.com\/openeuler/repo.openeuler.org/g" /etc/yum.repos.d/openEuler.repo && sed -i "s#http#https#g" /etc/yum.repos.d/openEuler.repo RUN sed -i "s/gpgcheck=1/gpgcheck=0/g" /etc/yum.repos.d/openEuler.repo && sed -i "/gpgkey=/d" /etc/yum.repos.d/openEuler.repo RUN cat /etc/yum.repos.d/openEuler.repo RUN yum install python3-pip binutils sshpass openssh-clients sudo git tar bc osc -y RUN pip3 install pyinstaller pexpect configparser requests lxml beautifulsoup4 threadpool pyyaml uuid kafka python-jenkins argparse xlwt paramiko -i https://pypi.tuna.tsinghua.edu.cn/simple
ARG VERSION=4.3 ARG user=jenkins ARG group=jenkins ARG uid=1000 ARG gid=1000 ARG AGENT_WORKDIR=/home/${user}/agent
RUN curl --create-dirs -fsSLo /usr/share/jenkins/agent.jar https://repo.jenkins-ci.org/public/org/jenkins-ci/main/remoting/${VERSION}/remoting-${VERSION}.jar \ && chmod 755 /usr/share/jenkins \ && chmod 644 /usr/share/jenkins/agent.jar \ && ln -sf /usr/share/jenkins/agent.jar /usr/share/jenkins/slave.jar
RUN curl --create-dirs -fsSLo /usr/local/bin/jenkins-agent http://121.36.53.23/AdoptOpenJDK/jenkins-agent #COPY jenkins-agent /usr/local/bin/jenkins-agent
RUN chmod a+rx /usr/local/openjdk-11 \ && chmod a+rx /usr/local/bin/jenkins-agent \ && ln -s /usr/local/bin/jenkins-agent /usr/local/bin/jenkins-slave
RUN groupadd -g ${gid} ${group} RUN useradd -c "Jenkins user" -d /home/${user} -u ${uid} -g ${gid} -m ${user} RUN echo "${user} ALL=(ALL) NOPASSWD:ALL" >> /etc/sudoers
USER ${user} ENV AGENT_WORKDIR=${AGENT_WORKDIR} RUN mkdir /home/${user}/.jenkins && mkdir -p ${AGENT_WORKDIR}
VOLUME /home/${user}/.jenkins VOLUME ${AGENT_WORKDIR} WORKDIR ${AGENT_WORKDIR}
ENTRYPOINT ["jenkins-agent"] FEOF }
#config_dockerfile config_obs_dockerfile set -x
