hudson.tasks.Shell.command:
#!/bin/bash
. /home/jenkins/ci_check/src/lib/lib.sh
function config_osc() { cat > /home/jenkins/.oscrc << EOF [general] apiurl = http://117.78.1.88 no_verify = 1 build-root = /home/jenkins/agent/buildroot
[http://117.78.1.88] user = $OBSUserName pass = $OBSPassword trusted_prj = openEuler:selfbuild:BaseOS openEuler:20.03:LTS:selfbuild:BaseOS openEuler:selfbuild:function openEuler:20.09:selfbuild:BaseOS # 不用输0,1,2了 EOF }
function download_buddy_repo() { # download buddy repo for item in $(echo ${buddy} |sed 's/,/ /g') do
if [[ "x$item" == "xopenEuler_kernel" || "x$item" == "xopenEuler-20.09_kernel" ]] then download_kernel_repo elif [ "x$item" != "x$repo" ] then log_info "clone ${item} of branch $tbranch" git clone -b $tbranch --depth 1 https://${GiteeUserName}:${GiteePassword}@gitee.com/src-openeuler/${item} fi done }
download_kernel_times=0 # 避免下载多次kernel function download_kernel_repo() { if [[ $download_kernel_times -gt 0 ]] then log_info "already clone kernel source" return fi
(( download_kernel_times += 1 ))
# 复用pod if [[ -d code/kernel ]] then rm -rf code/kernel fi
if [ "x$repo" = "xkernel" ] then kernel_tag=$(cat kernel/SOURCE) log_info "now clone kernel source of tag ${kernel_tag} to code/kernel" git clone -b $kernel_tag --depth 1 https://${GiteeUserName}:${GiteePassword}@gitee.com/openeuler/kernel code/kernel else log_info "now clone kernel source of branch ${tbranch}" git clone -b $tbranch --depth 1 https://${GiteeUserName}:${GiteePassword}@gitee.com/openeuler/kernel code/kernel fi
# 处理 "Installed (but unpackaged) file" 异常 rm -rf $WORKSPACE/code/kernel/.git }
# 复用pod，暴力清除 function drop_pod_cache() { rm -rf /tmp/local_code/xdf/repo
# 第一次使用时，没有mount文件系统 set +e mount | grep buildroot | awk '{print $3}' | sudo xargs umount &> /dev/null set -e
if [[ -d /home/jenkins/agent/buildroot ]] then sudo chmod -R 777 /home/jenkins/agent/buildroot # buildroot mode is 444, owner and group is root rm -rf /home/jenkins/agent/buildroot fi
# check abi comment if [[ -e $WORKSPACE/${repo}_${arch}_comment ]] then rm $WORKSPACE/${repo}_${arch}_comment fi }
function build_packages() { for item in $(echo ${package} |sed 's/,/ /g') do log_info "start build package $item" log_debug "params are [$repo, $branch, $prid, $committer, $arch, $package, $buddy, $WORKSPACE]"
export PYTHONPATH=/home/jenkins/ci_check python /home/jenkins/ci_check/src/build/osc_build_k8s.py -p $item -a $arch -c $WORKSPACE -b $tbranch
log_debug "check abi" output="${item}_check_abi_${BUILD_ID}.md" comment="${repo}_${arch}_comment" python3 /home/jenkins/ci_check/src/build/extra_work.py -r $tbranch -b $obs_rpm_host -s $obs_webui_host -f checkabi -p ${item} -c ${committer} -a ${arch} -l "http://${repo_server}" -o $WORKSPACE/${output} -e $WORKSPACE/${comment} || echo "continue although run check abi failed" # run before save rpm, arch dir not exist for first compile ssh -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR root@${repo_server} "mkdir -p /repo/openeuler/${committer}/${item}/${arch}/noarch" if [[ -e $output ]] then scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR ${output} root@${repo_server}:/repo/openeuler/${committer}/${item}/${arch}/${output} fi if [[ -e $comment ]] then scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR ${comment} root@${repo_server}:/repo/soe/check_abi/${comment} fi
log_debug "save result" save_build_result $package
log_debug "pkgship notify" if [[ "x$item" == "xpkgship" ]] then scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null root@${repo_server}:/repo/soe/pkgship pkgship_notify python /home/jenkins/ci_check/src/build/extra_work.py -f notify -p ${item} -a ${arch} -n ${pkgship_notify_url} -t ${pkgship_notify_token} -l "http://${repo_server}" -m pkgship_notify -u ${PkgShipUserName} -w ${PkgShipPassword} || echo "continue although run pkgship notify error" fi done }
function save_build_result() { ssh -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR root@${repo_server} "mkdir -p /repo/openeuler/${committer}/${1}/${arch}/noarch" if [[ -d /home/jenkins/agent/buildroot/home/abuild/rpmbuild/RPMS/${arch} && "$(ls -A /home/jenkins/agent/buildroot/home/abuild/rpmbuild/RPMS/${arch})" ]] then scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR /home/jenkins/agent/buildroot/home/abuild/rpmbuild/RPMS/${arch}/* root@${repo_server}:/repo/openeuler/${committer}/${1}/${arch}
# last build packages soft link for rpm in $(ls -A /home/jenkins/agent/buildroot/home/abuild/rpmbuild/RPMS/${arch}) do linker=${rpm%%-[0-9]*m} # 获取包名，去掉版本号后的内容 ssh -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR root@${repo_server} "ln -sf /repo/openeuler/${committer}/${1}/${arch}/$rpm /repo/openeuler/0X080480000XC0000000/${arch}/${linker}" done fi
if [[ -d /home/jenkins/agent/buildroot/home/abuild/rpmbuild/RPMS/noarch && "$(ls -A /home/jenkins/agent/buildroot/home/abuild/rpmbuild/RPMS/noarch)" ]] then scp -r -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR /home/jenkins/agent/buildroot/home/abuild/rpmbuild/RPMS/noarch/* root@${repo_server}:/repo/openeuler/${committer}/${1}/${arch}/noarch
# last build packages soft link for rpm in $(ls -A /home/jenkins/agent/buildroot/home/abuild/rpmbuild/RPMS/noarch) do linker=${rpm%%-[0-9]*m} ssh -i ${SaveBuildRPM2Repo} -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o LogLevel=ERROR root@${repo_server} "ln -sf /repo/openeuler/${committer}/${1}/noarch/$rpm /repo/openeuler/0X080480000XC0000000/${arch}/${linker}" done fi }
set +x set -e config_osc download_buddy_repo drop_pod_cache build_packages set +e set -x
