#!/bin/bash function get_source(){ if [[ $1 = "20.03-LTS" ]];then git clone https://heruxiao%40huawei.com:Huawei12%23%24@gitee.com/KeyboardXia/kernel-san-20.03-LTS-SP1.git cd kernel-san-20.03-LTS-SP1 git filter-branch -f --tree-filter 'rm -rf *' --tag-name-filter cat -- --all git pull --rebase git push --all --force cd - wget https://gitee.com/openeuler/kernel/repository/archive/openEuler-1.0-LTS.zip unzip openEuler-1.0-LTS.zip -d ./kernel-san-20.03-LTS-SP1 rm -rf *.zip mkdir -p ./kernel-san-20.03-LTS-SP1/tmp #wget https://gitee.com/src-openeuler/kernel/repository/archive/openEuler-$1.zip wget https://gitee.com/src-openeuler/kernel/repository/archive/openEuler-20.03-LTS-SP1.zip unzip openEuler-20.03-LTS-SP1.zip -d ./kernel-san-20.03-LTS-SP1/tmp
mv ./kernel-san-20.03-LTS-SP1/tmp/kernel/* ./kernel-san-20.03-LTS-SP1 rm -rf *.zip ./kernel-san-20.03-LTS-SP1/tmp else git clone https://heruxiao%40huawei.com:Huawei12%23%24@gitee.com/KeyboardXia/kernel-san-20.09.git cd kernel-san-20.09 git filter-branch -f --tree-filter 'rm -rf *' --tag-name-filter cat -- --all git pull --rebase git push --all --force cd - wget https://gitee.com/openeuler/kernel/repository/archive/openEuler-$1.zip unzip openEuler-$1.zip -d ./kernel-san-20.09 rm -rf *.zip mkdir -p ./kernel-san-20.09/tmp wget https://gitee.com/src-openeuler/kernel/repository/archive/openEuler-$1.zip unzip openEuler-$1.zip -d ./kernel-san-20.09/tmp mv ./kernel-san-20.09/tmp/kernel/* ./kernel-san-20.09 rm -rf *.zip ./kernel-san-20.09/tmp
fi
}
function modify_kernel_config(){ sed -i "s/CONFIG_RANDOMIZE_BASE=y/# CONFIG_RANDOMIZE_BASE is not set/g" $1 sed -i "s/# CONFIG_DEBUG_KMEMLEAK is not set/CONFIG_DEBUG_KMEMLEAK=y/g" $1 sed -i "s/# CONFIG_KASAN is not set/CONFIG_KASAN=y\nCONFIG_KASANINLINE=y/g" $1 sed -i "s/CONFIG_ARCH_HAS_KCOV=y/CONFIG_ARCH_HAS_KCOV=y\nCONFIG_KCOV=y\nCONFIG_KCOV_INSTRUMENT_ALL=y\ \nCONFIG_KCOV_ENABLE_COMPARISONS=y/g" $1 sed -i "s/# CONFIG_FAULT_INJECTION is not set/CONFIG_FAULT_INJECTION=y\n\ CONFIG_FAULT_INJECTION_DEBUG_FS=y\nCONFIG_FAULT_INJECTION_USERCOPY=y\n\ CONFIG_FAILSLAB=y\nCONFIG_FAIL_PAGE_ALLOC=y/g" $1 } function update_gitee_repo(){ arm_config_file=kernel/arch/arm64/configs/openeuler_defconfig x86_config_file=kernel/arch/x86/configs/openeuler_defconfig cd kernel-san-$1 modify_kernel_config ${arm_config_file} modify_kernel_config ${x86_config_file} old_version=`grep %{hulkrelease} kernel.spec |grep Release |awk -F . '{ print $2}'` new_version=$old_version'.1' sed -i "s/$old_version/$new_version/g" kernel.spec git add -A git commit -m "update" git push cd - }
function branch_modify(){ git config --global user.email "heruxiao@huawei.com" git config --global user.name "horsefucker" if [[ -d kernel-san* ]];then rm -rf kernel-san* fi get_source 20.03-LTS update_gitee_repo 20.03-LTS-SP1 rm -rf ./openeuler_kernel_san get_source 20.09 update_gitee_repo 20.09 rm -rf ./openeuler_kernel_san
}
branch_modify
touch ~/.oscrc cat > ~/.oscrc <<EOF [general] apiurl = https://build.openeuler.org [https://build.openeuler.org] user = tester pass = Huawei12#$ EOF osc service remote run home:tester:branches:openEuler:Mainline kernel
