hudson.tasks.Shell.command:
function config_maven()
{
  cat > /home/jenkins/.m2 << EOF
<settings>
    <mirrors>
        <mirror>
            <id>huaweimaven</id>
      		<name>huaweicloud maven</name>
      		<url>https://mirrors.huaweicloud.com/repository/maven/</url>
      		<mirrorOf>central</mirrorOf> 
        </mirror>
    </mirrors>
</settings>
EOF
}

function config_gradle()
{
  cat > /home/jenkins/.gradle << EOF
allprojects {
    buildscript {
        repositories {
            mavenLocal()
            maven { url "https://mirrors.huaweicloud.com/repository/maven/" }
        }
    }
    repositories {
        mavenLocal()
        maven { url "https://mirrors.huaweicloud.com/repository/maven/" }
    }
}
EOF
}

config_maven
config_gradle
sudo yum install maven -y

mvn help:effective-settings


cat /home/jenkins/.gradle
cat /home/jenkins/.m2
